#include "SenseBoxMCU.h"

const long interval = 500;
long time_start = 0;
long time_actual = 0;

BMP280 bmp_sensor;





void setup() {
SerialUSB.begin(9600);bmp_sensor.begin();

}


void loop() {
time_start = millis();
if (time_start > time_actual + interval) {
  time_actual = millis();
  SerialUSB.println(bmp_sensor.getTemperature());
}

}

#include <SPI.h>
#include <Wire.h>
#include <Adafruit_GFX.h>
#include <Adafruit_SSD1306.h>
#include "SenseBoxMCU.h"

const long interval = 50;
long time_start = 0;
long time_actual = 0;

#define OLED_RESET 4
Adafruit_SSD1306 display(OLED_RESET);
BMP280 bmp_sensor;


    void printOnDisplay(String title1, String measurement1, String unit1, String title2, String measurement2, String unit2) {

      display.setCursor(0, 0);
      display.setTextSize(1);
      display.setTextColor(WHITE, BLACK);
      display.println(title1);
      display.setCursor(0, 10);
      display.setTextSize(2);
      display.print(measurement1);
      display.print(" ");
      display.setTextSize(1);
      display.println(unit1);
      display.setCursor(0, 30);
      display.setTextSize(1);
      display.println(title2);
      display.setCursor(0, 40);
      display.setTextSize(2);
      display.print(measurement2);
      display.print(" ");
      display.setTextSize(1);
      display.println(unit2);
    }





void setup() {
senseBoxIO.powerI2C(true);
delay(2000);
display.begin(SSD1306_SWITCHCAPVCC, 0x3D);
display.display();
delay(100);
display.clearDisplay();SerialUSB.begin(9600);bmp_sensor.begin();

}


void loop() {
time_start = millis();
if (time_start > time_actual + interval) {
  time_actual = millis();
  SerialUSB.println(bmp_sensor.getTemperature());
     printOnDisplay("Temperatur", String(bmp_sensor.getTemperature()), " ", " ", String(), " ");
  display.display();
}

}

